// =========================================================
// CONFIGURA√á√ïES CUSTOMIZ√ÅVEIS
// =========================================================
const CONFIG = {
  maxAppointmentsPerEmail: -1,    // -1 para desativar, 1 para um agendamento por email
  daysToCheck: 30,               // Per√≠odo para verificar agendamentos
  timezone: 'America/Sao_Paulo', // Fuso hor√°rio padr√£o
  restrictByEmail: true,         // Se true, requer email para todas opera√ß√µes
  defaultDuration: 60            // Dura√ß√£o padr√£o em minutos
};

// Obter credenciais do sistema
const inputMapping = $('Core Input').item.json.body.mapping;
const systemData = JSON.parse(inputMapping).system;
const credentials = {
  accessToken: systemData.accessToken,
  refreshToken: systemData.refreshToken,
  clientId: systemData.clientId,
  clientSecret: systemData.clientSecret,
  directusToken: systemData.directus_token,
  directusService: systemData.directus_service
};

// Input do usu√°rio e array de logs
const inputData = query;
const logs = [];

// Configura√ß√£o do dayjs
const dayjs = require('dayjs');
const utc = require('dayjs/plugin/utc');
const timezone = require('dayjs/plugin/timezone');
const weekday = require('dayjs/plugin/weekday');
const isoWeek = require('dayjs/plugin/isoWeek');
const dayOfYear = require('dayjs/plugin/dayOfYear');
const quarterOfYear = require('dayjs/plugin/quarterOfYear');
const customParseFormat = require('dayjs/plugin/customParseFormat');
const isBetween = require('dayjs/plugin/isBetween');

// Configurar plugins do dayjs
dayjs.extend(utc);
dayjs.extend(timezone);
dayjs.extend(weekday);
dayjs.extend(isoWeek);
dayjs.extend(dayOfYear);
dayjs.extend(quarterOfYear);
dayjs.extend(customParseFormat);
dayjs.extend(isBetween);

// Configurar timezone
dayjs.tz.setDefault(CONFIG.timezone);

// =========================================================
// FUN√á√ïES AUXILIARES
// =========================================================

// Fun√ß√£o para logging
function log(message, data = null) {
  const entry = {
    timestamp: new Date().toISOString(),
    message
  };

  if (data) {
    if (typeof data === 'string' && data.length > 200) {
      entry.data = data.substring(0, 200) + '... (truncado)';
    } else if (typeof data === 'object') {
      const stringified = JSON.stringify(data);
      entry.data = stringified.length > 200 
        ? JSON.stringify(data).substring(0, 200) + '... (truncado)' 
        : data;
    } else {
      entry.data = data;
    }
  }

  logs.push(entry);
}

// Fun√ß√£o para verificar se o email √© obrigat√≥rio e foi fornecido
function validateEmailRequirement() {
  if (CONFIG.restrictByEmail && !inputData.attendee_email) {
    throw new Error("Email obrigat√≥rio: Quando RESTRICT_OPERATIONS_BY_EMAIL est√° ativado, o campo 'attendee_email' √© obrigat√≥rio");
  }
}

// Fun√ß√£o para renovar o token
async function refreshGoogleToken() {
  log('Renovando token do Google');

  try {
    const axios = require('axios');
    const qs = require('querystring');

    const response = await axios({
      method: 'post',
      url: 'https://oauth2.googleapis.com/token',
      headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
      data: qs.stringify({
        client_id: credentials.clientId,
        client_secret: credentials.clientSecret,
        refresh_token: credentials.refreshToken,
        grant_type: 'refresh_token'
      })
    });

    const newToken = response.data.access_token;

    // Atualizar token no Directus
    await axios({
      method: 'patch',
      url: `http://${credentials.directusService}:8055/items/global_vars`,
      headers: {
        'Authorization': `Bearer ${credentials.directusToken}`,
        'Content-Type': 'application/json'
      },
      data: { accessToken: newToken }
    });

    log('Token renovado e atualizado com sucesso');
    return newToken;
  } catch (error) {
    log('Erro ao renovar token', error.message);
    throw error;
  }
}

// Fun√ß√£o para obter o ID do calend√°rio
function getCalendarId() {
  return (!inputData.calendar_id || inputData.calendar_id === '') ? 'primary' : inputData.calendar_id;
}

// Fun√ß√£o para executar opera√ß√µes com renova√ß√£o autom√°tica de token
async function executeWithToken(operation) {
  try {
    validateEmailRequirement();
    return await operation(credentials.accessToken);
  } catch (error) {
    // Se for erro de token expirado, renovar e tentar novamente
    if (error.response && error.response.status === 401) {
      log('Token expirado, renovando e tentando novamente');
      const newToken = await refreshGoogleToken();
      const result = await operation(newToken);
      return {
        ...result,
        message: result.message + " (ap√≥s renova√ß√£o do token)"
      };
    }

    // Outros erros
    const errorMsg = error.message || 'Erro desconhecido';
    let errorType = "general_error";

    if (errorMsg.includes("Limite m√°ximo")) errorType = "appointment_limit_exceeded";
    if (errorMsg.includes("Acesso negado") || errorMsg.includes("Email obrigat√≥rio")) errorType = "access_denied";

    throw { message: errorMsg, errorType };
  }
}

// =========================================================
// PROCESSAMENTO DE TEMPO RELATIVO
// =========================================================

// Fun√ß√£o para verificar se √© dia √∫til
function isBusinessDay(date) {
  const dayOfWeek = date.day();
  return dayOfWeek !== 0 && dayOfWeek !== 6; // 0 = domingo, 6 = s√°bado
}

// Fun√ß√£o para ajustar para o pr√≥ximo dia √∫til
function adjustToBusinessDay(date) {
  if (isBusinessDay(date)) return date;
  return date.day() === 6 ? date.add(2, 'day') : date.add(1, 'day');
}

// Fun√ß√£o para calcular data/hora a partir de refer√™ncias relativas
function calculateDateTime() {
  // Se hor√°rios exatos forem fornecidos, us√°-los diretamente
  if (inputData.exact_start_time) {
    return {
      startTime: dayjs(inputData.exact_start_time).tz(CONFIG.timezone),
      endTime: inputData.exact_end_time 
        ? dayjs(inputData.exact_end_time).tz(CONFIG.timezone)
        : dayjs(inputData.exact_start_time).tz(CONFIG.timezone).add(inputData.duration_minutes || CONFIG.defaultDuration, 'minute')
    };
  }

  // Iniciar com a data/hora base
  let baseDate = dayjs().tz(CONFIG.timezone);

  // Aplicar a refer√™ncia temporal base
  if (inputData.time_reference) {
    switch (inputData.time_reference) {
      case 'now': 
        // J√° estamos usando now como base
        break;
      case 'today': 
        baseDate = baseDate.startOf('day');
        break;
      case 'this_week': 
        baseDate = baseDate.startOf('week');
        break;
      case 'this_month': 
        baseDate = baseDate.startOf('month');
        break;
      case 'this_quarter': 
        baseDate = baseDate.startOf('quarter');
        break;
      case 'this_year': 
        baseDate = baseDate.startOf('year');
        break;
    }
  }

  // Aplicar deslocamento temporal (offset)
  if (typeof inputData.offset_value === 'number' && inputData.offset_unit) {
    baseDate = baseDate.add(inputData.offset_value, inputData.offset_unit);
  }

  // Aplicar m√™s do ano, se especificado
  if (typeof inputData.month_of_year === 'number') {
    baseDate = baseDate.month(inputData.month_of_year - 1); // Meses em dayjs s√£o 0-11
  }

  // Aplicar trimestre do ano, se especificado
  if (typeof inputData.quarter_of_year === 'number') {
    const startMonth = (inputData.quarter_of_year - 1) * 3;
    baseDate = baseDate.month(startMonth).startOf('month');
  }

  // Aplicar dia do m√™s
  if (typeof inputData.day_of_month === 'number') {
    if (inputData.day_of_month === -1) {
      baseDate = baseDate.endOf('month').startOf('day');
    } else {
      baseDate = baseDate.date(inputData.day_of_month);
    }
  }

  // Aplicar dia da semana e semana do m√™s
  if (typeof inputData.day_of_week === 'number') {
    if (typeof inputData.week_of_month === 'number') {
      // Voltar para o in√≠cio do m√™s atual
      let monthStart = baseDate.startOf('month');

      if (inputData.week_of_month === -1) {
        // √öltima semana do m√™s
        let monthEnd = baseDate.endOf('month');
        while (monthEnd.day() !== inputData.day_of_week) {
          monthEnd = monthEnd.subtract(1, 'day');
        }
        baseDate = monthEnd;
      } else {
        // Encontrar o primeiro dia da semana especificado no m√™s
        let firstOccurrence = monthStart.day(inputData.day_of_week);

        // Se estivermos no m√™s anterior, avan√ßar uma semana
        if (firstOccurrence.month() !== monthStart.month()) {
          firstOccurrence = firstOccurrence.add(7, 'day');
        }

        // Avan√ßar para a semana espec√≠fica
        baseDate = firstOccurrence.add((inputData.week_of_month - 1) * 7, 'day');
      }
    } else {
      // Somente dia da semana especificado, ir para o pr√≥ximo
      baseDate = baseDate.day(inputData.day_of_week);

      // Se j√° passou esta semana, avan√ßar uma semana
      if (baseDate.isBefore(dayjs().tz(CONFIG.timezone))) {
        baseDate = baseDate.add(7, 'day');
      }
    }
  }

  // Ajustar para dia √∫til se necess√°rio
  if (inputData.business_days_only === true) {
    baseDate = adjustToBusinessDay(baseDate);
  }

  // Aplicar hora e minuto
  if (typeof inputData.hour_of_day === 'number') {
    baseDate = baseDate.hour(inputData.hour_of_day).minute(typeof inputData.minute_of_hour === 'number' ? inputData.minute_of_hour : 0);
  } else if (typeof inputData.minute_of_hour === 'number') {
    baseDate = baseDate.minute(inputData.minute_of_hour);
  }

  // Se nem hora nem minuto foram especificados, usar hora atual
  if (typeof inputData.hour_of_day !== 'number' && typeof inputData.minute_of_hour !== 'number') {
    const now = dayjs().tz(CONFIG.timezone);
    baseDate = baseDate.hour(now.hour()).minute(now.minute());
  }

  // Zerar segundos e milissegundos
  baseDate = baseDate.second(0).millisecond(0);

  // Calcular hora de t√©rmino (in√≠cio + dura√ß√£o)
  const duration = inputData.duration_minutes || CONFIG.defaultDuration;
  const endDate = baseDate.add(duration, 'minute');

  log('Data/hora calculada', {
    start: baseDate.format(),
    end: endDate.format()
  });

  return {
    startTime: baseDate,
    endTime: endDate
  };
}

// =========================================================
// FUN√á√ïES DE API DO GOOGLE CALENDAR
// =========================================================

// Cliente HTTP reutiliz√°vel
const createClient = (token) => {
  const axios = require('axios');
  return {
    get: (url, params = {}) => axios({
      method: 'get',
      url,
      headers: { 'Authorization': `Bearer ${token}` },
      params
    }),
    post: (url, data) => axios({
      method: 'post',
      url,
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      },
      data
    }),
    put: (url, data) => axios({
      method: 'put',
      url,
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      },
      data
    }),
    delete: (url) => axios({
      method: 'delete',
      url,
      headers: { 'Authorization': `Bearer ${token}` }
    })
  };
};

// Verificar limite de agendamentos por email
async function checkAppointmentsForEmail(token, email, calendarId) {
  if (CONFIG.maxAppointmentsPerEmail === -1) return 0;

  log(`Verificando agendamentos para ${email}`);

  try {
    const client = createClient(token);
    const now = new Date();
    const pastDate = new Date(now);
    pastDate.setDate(now.getDate() - CONFIG.daysToCheck);

    const response = await client.get(
      `https://www.googleapis.com/calendar/v3/calendars/${encodeURIComponent(calendarId)}/events`,
      {
        timeMin: pastDate.toISOString(),
        timeMax: new Date(now.getTime() + (365 * 24 * 60 * 60 * 1000)).toISOString(),
        q: email,
        singleEvents: true
      }
    );

    // Filtrar eventos onde o email √© participante
    const relevantEvents = response.data.items.filter(event => 
      event.attendees?.some(attendee => attendee.email.toLowerCase() === email.toLowerCase())
    );

    log(`Encontrados ${relevantEvents.length} agendamentos para ${email}`);
    return relevantEvents.length;
  } catch (error) {
    log('Erro ao verificar agendamentos', error.message);
    return 0; // Em caso de erro, n√£o bloqueamos
  }
}

// Buscar eventos
async function findEvents(token, params) {
  const { email, eventTitle, calendarId, timeMin, timeMax, maxResults } = params;

  log(`Buscando eventos ${eventTitle ? `com t√≠tulo "${eventTitle}" ` : ''}${email ? `para ${email}` : ''}`);

  try {
    const client = createClient(token);

    // Preparar datas padr√£o se n√£o fornecidas
    const now = new Date();
    const defaultPastDate = new Date(now);
    defaultPastDate.setDate(now.getDate() - CONFIG.daysToCheck);

    const searchTimeMin = timeMin || defaultPastDate.toISOString();
    const searchTimeMax = timeMax || new Date(now.getTime() + (365 * 24 * 60 * 60 * 1000)).toISOString();

    // Termo de busca
    const queryParam = eventTitle && email ? `${email} ${eventTitle}` : 
                       email ? email : 
                       eventTitle ? eventTitle : '';

    const response = await client.get(
      `https://www.googleapis.com/calendar/v3/calendars/${encodeURIComponent(calendarId)}/events`,
      {
        timeMin: searchTimeMin,
        timeMax: searchTimeMax,
        q: queryParam,
        singleEvents: true,
        maxResults: maxResults || 10
      }
    );

    let events = response.data.items || [];

    // Filtrar por email se necess√°rio
    if (CONFIG.restrictByEmail && email) {
      events = events.filter(event => 
        event.attendees?.some(attendee => attendee.email.toLowerCase() === email.toLowerCase())
      );
    }

    // Filtrar por t√≠tulo se fornecido
    if (eventTitle) {
      events = events.filter(event => 
        event.summary && event.summary.toLowerCase().includes(eventTitle.toLowerCase())
      );
    }

    log(`Encontrados ${events.length} eventos`);
    return events;
  } catch (error) {
    log('Erro ao buscar eventos', error.message);
    throw error;
  }
}

// Buscar evento por ID
async function getEventById(token, eventId, calendarId) {
  log(`Buscando evento com ID: ${eventId}`);

  try {
    const client = createClient(token);
    const response = await client.get(
      `https://www.googleapis.com/calendar/v3/calendars/${encodeURIComponent(calendarId)}/events/${encodeURIComponent(eventId)}`
    );
    return response.data;
  } catch (error) {
    log('Erro ao buscar evento por ID', error.message);
    throw error;
  }
}

// Verificar acesso ao evento
async function verifyEventAccess(token, eventId, email, calendarId) {
  if (!CONFIG.restrictByEmail || !email) return true;

  const event = await getEventById(token, eventId, calendarId);

  const isAttendee = event.attendees?.some(attendee => 
    attendee.email.toLowerCase() === email.toLowerCase()
  );

  if (!isAttendee) {
    throw new Error(`Acesso negado: ${email} n√£o √© um participante deste evento`);
  }

  return true;
}

// =========================================================
// OPERA√á√ïES DO CALEND√ÅRIO
// =========================================================

// Criar evento
async function createEvent(token) {
  const calendarId = getCalendarId();
  const { title, description, attendee_email } = inputData;

  if (!title) {
    throw new Error("Para criar um evento, forne√ßa um t√≠tulo");
  }

  // Calcular hor√°rios
  const { startTime, endTime } = calculateDateTime();

  // Verificar limite de agendamentos
  if (CONFIG.maxAppointmentsPerEmail > 0 && attendee_email) {
    const count = await checkAppointmentsForEmail(token, attendee_email, calendarId);
    if (count >= CONFIG.maxAppointmentsPerEmail) {
      throw new Error(`Limite m√°ximo de ${CONFIG.maxAppointmentsPerEmail} agendamento(s) por email excedido.`);
    }
  }

  // Preparar evento
  const event = {
    summary: title,
    description: description || '',
    start: {
      dateTime: startTime.toISOString(),
      timeZone: CONFIG.timezone
    },
    end: {
      dateTime: endTime.toISOString(),
      timeZone: CONFIG.timezone
    }
  };

  // Adicionar participante
  if (attendee_email) {
    event.attendees = [{
      email: attendee_email,
      responseStatus: 'needsAction'
    }];
  }

  // Criar evento
  log('Criando evento', { title, calendarId });
  const client = createClient(token);

  const response = await client.post(
    `https://www.googleapis.com/calendar/v3/calendars/${encodeURIComponent(calendarId)}/events`,
    event
  );

  return {
    success: true,
    message: "Evento criado com sucesso",
    eventId: response.data.id,
    eventLink: response.data.htmlLink,
    startTime: startTime.format(),
    endTime: endTime.format()
  };
}

// Atualizar evento
async function updateEvent(token) {
  const calendarId = getCalendarId();
  const { event_id, event_title, attendee_email, title, description } = inputData;

  // Validar dados
  if (!event_id && !event_title && !attendee_email) {
    throw new Error("Para atualizar um evento, forne√ßa identifica√ß√£o (ID ou t√≠tulo) e email");
  }

  if (!title && !description && !inputData.exact_start_time && !inputData.exact_end_time && 
      !inputData.time_reference && !inputData.day_of_week && !inputData.offset_value) {
    throw new Error("Para atualizar um evento, forne√ßa pelo menos um campo para atualizar");
  }

  // Encontrar o evento
  let eventId = event_id;

  if (!eventId) {
    const events = await findEvents(token, {
      email: attendee_email,
      eventTitle: event_title,
      calendarId
    });

    if (events.length === 0) {
      throw new Error("Nenhum evento encontrado com os crit√©rios fornecidos");
    }

    eventId = events[0].id;
    log(`Usando evento encontrado: ${eventId}`);
  }

  // Verificar acesso
  await verifyEventAccess(token, eventId, attendee_email, calendarId);

  // Buscar dados atuais
  const existingEvent = await getEventById(token, eventId, calendarId);

  // Calcular novos hor√°rios se necess√°rio
  let startDateTime = dayjs(existingEvent.start.dateTime || existingEvent.start.date).tz(CONFIG.timezone);
  let endDateTime = dayjs(existingEvent.end.dateTime || existingEvent.end.date).tz(CONFIG.timezone);

  // Se temos par√¢metros de tempo, recalcular
  if (inputData.exact_start_time || inputData.time_reference || inputData.offset_value || 
      inputData.day_of_week || inputData.hour_of_day) {
    const { startTime, endTime } = calculateDateTime();
    startDateTime = startTime;
    endDateTime = endTime;
  }

  // Preparar atualiza√ß√µes
  const updatedEvent = {
    ...existingEvent,
    summary: title || existingEvent.summary,
    description: description !== undefined ? description : existingEvent.description,
    start: {
      dateTime: startDateTime.toISOString(),
      timeZone: CONFIG.timezone
    },
    end: {
      dateTime: endDateTime.toISOString(),
      timeZone: CONFIG.timezone
    }
  };

  // Adicionar novo participante
  if (attendee_email && !existingEvent.attendees?.some(a => a.email.toLowerCase() === attendee_email.toLowerCase())) {
    updatedEvent.attendees = [
      ...(existingEvent.attendees || []),
      { email: attendee_email, responseStatus: 'needsAction' }
    ];
  }

  // Atualizar evento
  log('Atualizando evento', { eventId });
  const client = createClient(token);

  const response = await client.put(
    `https://www.googleapis.com/calendar/v3/calendars/${encodeURIComponent(calendarId)}/events/${encodeURIComponent(eventId)}`,
    updatedEvent
  );

  return {
    success: true,
    message: "Evento atualizado com sucesso",
    eventId: response.data.id,
    eventLink: response.data.htmlLink,
    startTime: startDateTime.format(),
    endTime: endDateTime.format()
  };
}

// Deletar evento
async function deleteEvent(token) {
  const calendarId = getCalendarId();
  const { event_id, event_title, attendee_email } = inputData;

  if (!event_id && !event_title && !attendee_email) {
    throw new Error("Para deletar um evento, forne√ßa o ID do evento ou crit√©rios de busca (t√≠tulo e/ou email)");
  }

  // Encontrar o evento
  let eventId = event_id;
  let eventSummary;

  if (!eventId) {
    const events = await findEvents(token, {
      email: attendee_email,
      eventTitle: event_title,
      calendarId
    });

    if (events.length === 0) {
      throw new Error("Nenhum evento encontrado com os crit√©rios fornecidos");
    }

    eventId = events[0].id;
    eventSummary = events[0].summary;
  } else {
    // Verificar acesso por ID
    const event = await getEventById(token, eventId, calendarId);
    eventSummary = event.summary;
    await verifyEventAccess(token, eventId, attendee_email, calendarId);
  }

  // Deletar evento
  log(`Deletando evento: ${eventId}`);
  const client = createClient(token);

  await client.delete(
    `https://www.googleapis.com/calendar/v3/calendars/${encodeURIComponent(calendarId)}/events/${encodeURIComponent(eventId)}`
  );

  return {
    success: true,
    message: "Evento deletado com sucesso",
    eventId,
    eventSummary
  };
}

// Consultar eventos
async function queryEvents(token) {
  const calendarId = getCalendarId();
  const { attendee_email, event_title, time_min, time_max, max_results } = inputData;

  // Buscar eventos
  const events = await findEvents(token, {
    email: attendee_email,
    eventTitle: event_title,
    calendarId,
    timeMin: time_min,
    timeMax: time_max,
    maxResults: max_results
  });

  // Simplificar eventos para o retorno
  const simplifiedEvents = events.map(event => ({
    id: event.id,
    title: event.summary,
    description: event.description,
    start_time: event.start?.dateTime || event.start?.date,
    end_time: event.end?.dateTime || event.end?.date,
    attendees: event.attendees?.map(att => att.email) || [],
    htmlLink: event.htmlLink
  }));

  return {
    success: true,
    message: `${events.length} evento(s) encontrado(s)`,
    count: events.length,
    events: simplifiedEvents
  };
}

// =========================================================
// EXECU√á√ÉO PRINCIPAL
// =========================================================

async function execute() {
  try {
    // Verificar opera√ß√£o v√°lida
    const operations = {
      'create': createEvent,
      'update': updateEvent,
      'delete': deleteEvent,
      'query': queryEvents
    };

    if (!inputData.operation || !operations[inputData.operation]) {
      throw new Error("Opera√ß√£o inv√°lida. Use 'create', 'update', 'delete' ou 'query'");
    }

    // Executar com tratamento de token
    const result = await executeWithToken(operations[inputData.operation]);

    return JSON.stringify({
      ...result,
      logs
    });
  } catch (error) {
    return JSON.stringify({
      success: false,
      message: error.message || "Erro desconhecido",
      errorType: error.errorType || "general_error",
      logs
    });
  }
}

// Iniciar execu√ß√£o
return execute();

