// Obt√©m todos os itens de entrada
const items = $input.all();

// Filtra os itens, mantendo apenas aqueles cujo campo body.content n√£o cont√©m apenas emojis
const filteredItems = items.filter(item => {
  // Acessa o campo content dentro de body
  const content = item.json.body && item.json.body.content ? item.json.body.content : '';

  // Se o conte√∫do estiver vazio, bloquear
  if (!content) {
    return false;
  }

  // Regex expandida para detectar emojis, incluindo cora√ß√µes, estrelas e outros s√≠mbolos
  const emojiRegex = /[\u{1F000}-\u{1FFFF}\u{2600}-\u{26FF}\u{2700}-\u{27BF}\u{FE00}-\u{FE0F}\u{2B50}]/u;

  // Remove emojis do conte√∫do
  const textWithoutEmojis = content.replace(emojiRegex, '');

  // Remove espa√ßos, pontua√ß√£o e outros caracteres n√£o alfanum√©ricos
  const cleanText = textWithoutEmojis.replace(/[^\w]/g, '').trim();

  // Retorna true (mant√©m o item) se houver texto al√©m de emojis ou se n√£o houver emojis
  return cleanText.length > 0 || !emojiRegex.test(content);
});

// Retorna os itens filtrados
return filteredItems;
